Quote.create(source:"Fyodor Dostoyevsky",philosophy:"Existentialism",quote:"The soul is healed by being with children.",_id:"2HxACEhfZ8DkuNnZ")
Quote.create(source:"Rumi",philosophy:"Mysticism",quote:"Don't be satisfied with stories, how things have gone with others. Unfold your own myth.",_id:"2Wqp3NV8XvZs2HKY")
